:root{--system-fonts:-apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,Helvetica,Arial,sans-serif,"Apple Color Emoji","Segoe UI Emoji","Segoe UI Symbol";--html-background-color:#ddd;--body-background-color:#fff;--stark-background-color:#111;--base-font-color:#333;--accent-font-color:#757575;--faint-font-color:#E9E9E9;--background-color:#e3f2f9;--background-accent-color:#c6e7f5;--primary-accent-color:#CA4246;--secondary-accent-color:#256EFF}@media (prefers-color-scheme:dark){:root{--html-background-color:#555;--body-background-color:#1a1a1a;--stark-background-color:#eee;--base-font-color:#ddd;--accent-font-color:#828282;--faint-font-color:#333;--background-color:#144962;--background-accent-color:#02374e}}*{box-sizing:border-box}html{font-size:62.5%;min-height:100%;background:var(--html-background-color)}body{font-family:Montserrat,sans-serif;line-height:1.6;background:var(--body-background-color);color:var(--base-font-color);font-size:clamp(1.6rem,2.5vw,2rem);margin:0;padding:0;-webkit-font-smoothing:antialiased}.wrapper{overflow:hidden}.content{max-width:60ch;margin:0 auto;padding:2rem}#header{background:linear-gradient(27deg,var(--background-accent-color) 0,var(--background-color) 30%,var(--background-color) 70%,var(--background-accent-color) 100%);border-bottom:solid 2px var(--background-accent-color);padding:min(1vw,1rem) 0}#colophon{color:var(--base-font-color);background-color:var(--background-color);border-top:solid 2px var(--background-accent-color)}#colophon .main-navigation,#colophon .site-info{font-size:1.6rem}.page-title{font-size:3rem;line-height:1.1;margin:0;padding-top:6px}#header .page-description,#header .site-header-menu{font-size:1.4rem;margin:0}#header header{margin-bottom:2rem}.footer-menus{margin-bottom:4rem}.site-info a{text-decoration:none}.site-info>span:not(:last-child):after{content:"";margin:0 1rem}.copyright{font-weight:700}.copyright a{color:var(--base-font-color)}@media only screen and (max-width:750px){.content{padding:1rem 2rem}}.entry-header{margin-top:2rem}.entry-footer{font-size:.8em;padding-top:1em;text-decoration:none;color:var(--accent-font-color);clear:both}.entry-footer{margin-top:1rem}.post-content{margin:2rem 0}.nav-links{display:flex}.nav-next{text-align:right}.nav-next,.nav-previous{width:50%}@media only screen and (max-width:750px){.nav-links{display:block}.nav-next,.nav-previous{padding:1rem 0;width:100%}}a{color:var(--primary-accent-color)}#header a,footer a{color:var(--base-font-color)}h1 a{text-decoration:none}h1{margin:1em 0 .25em;line-height:1.4;font-weight:600;word-wrap:break-word}h1{font-size:200%}h1.entry-title{font-size:1.5em}h1.entry-title{margin-bottom:.1em}img{max-width:100%;max-height:1200px}.social-navigation a:before{-moz-osx-font-smoothing:grayscale;-webkit-font-smoothing:antialiased;display:inline-block;font-family:social-logos;font-size:24px;font-style:normal;font-variant:normal;font-weight:400;line-height:20px;speak:none;text-align:center;text-decoration:inherit;text-transform:none;vertical-align:top}.main-menu{margin:1rem 0;padding:0;display:flex;flex-grow:1;flex-wrap:wrap}.main-menu li{list-style:none}.main-menu a{text-decoration:none}.main-navigation .menu-item:not(:last-child):after{content:'\b7';padding:1rem}:root{--color-bluesky:#3B84F6;--color-instagram:#d93174;--color-linkedin:#0976b4;--color-mastodon:#6364ff;--color-medium:#12100e;--color-pocket:#ee4256;--color-print:#f8f8f8;--color-reddit:#5f99cf;--color-skype:#00aff0;--color-tumblr:#35465c;--color-whatsapp:#43d854;--color-x:#000}.social-navigation ul{list-style:none;margin:0 0 -.4375em;display:flex;flex-grow:1;flex-wrap:wrap;padding:0}.social-navigation li{margin:0 .4375em .4375em 0}.social-navigation a{color:var(--base-font-color);display:block;position:relative;text-decoration:none;margin:3px}.social-navigation a:before{content:"\f415";text-align:center;padding:2px;border-radius:3px}.social-navigation a[href*="github.com"]:before{content:"\f200"}.social-navigation a[href*="linkedin.com"]:before{content:"\f207"}.social-navigation a[href*="/feed"]:before{content:"\f413";font-size:20px;background-color:#db7e36;color:#fff}.social-navigation a[href*="indieweb.social"]:before{content:"\f10a"}.social-navigation a[href*="bsky.app"]:before{content:"\f10f"}.avatar{line-height:4rem;border-radius:50%;margin-right:2rem;position:relative;float:left;width:60px;height:60px}#header .avatar{width:66px;height:66px;border:solid 3px var(--background-accent-color)}.screen-reader-text{border:0;clip:rect(1px,1px,1px,1px);clip-path:inset(50%);height:1px;margin:-1px;overflow:hidden;padding:0;position:absolute!important;width:1px;word-wrap:normal!important}